import './App.css';
import './index.css';
import { useState } from 'react';
import ScrollReveal from './ScrollReveal';
import ResultsReveal from './ResultsReveal';
import * as React from "react";
import { Card, CardContent } from "@shadcn/ui/card";
import { Input } from "@shadcn/ui/input";
import { Label } from "@shadcn/ui/label";
import { Button } from "@shadcn/ui/button";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@shadcn/ui/tabs";
import { Separator } from "@shadcn/ui/separator";
import { Zap, Bolt, Gauge, Clock, RotateCcw, Calculator, ArrowRight } from "lucide-react";

const PowerCalculator = () => {
  const [voltage, setVoltage] = useState("");
  const [current, setCurrent] = useState("");
  const [resistance, setResistance] = useState("");
  const [power, setPower] = useState("");
  const [time, setTime] = useState("");
  const [result, setResult] = useState({});
  const [activeTab, setActiveTab] = useState("power");
  const [error, setError] = useState(null);

  const resetForm = () => {
    setVoltage("");
    setCurrent("");
    setResistance("");
    setPower("");
    setTime("");
    setResult({});
    setError(null);
  };

  const calculatePower = () => {
    setError(null);
    const v = parseFloat(voltage);
    const i = parseFloat(current);
    const r = parseFloat(resistance);
    const p = parseFloat(power);
    const t = parseFloat(time);
    try {
      const newResult = {};
      switch (activeTab) {
        case "power":
          if (!isNaN(v) && !isNaN(i)) {
            newResult.power = v * i;
          } else if (!isNaN(v) && !isNaN(r) && r !== 0) {
            newResult.power = (v * v) / r;
          } else if (!isNaN(i) && !isNaN(r)) {
            newResult.power = i * i * r;
          } else {
            throw new Error("Please provide either voltage and current, voltage and resistance, or current and resistance");
          }
          break;
        case "voltage":
          if (!isNaN(p) && !isNaN(i) && i !== 0) {
            newResult.voltage = p / i;
          } else if (!isNaN(p) && !isNaN(r)) {
            newResult.voltage = Math.sqrt(p * r);
          } else if (!isNaN(i) && !isNaN(r)) {
            newResult.voltage = i * r;
          } else {
            throw new Error("Please provide either power and current, power and resistance, or current and resistance");
          }
          break;
        case "current":
          if (!isNaN(p) && !isNaN(v) && v !== 0) {
            newResult.current = p / v;
          } else if (!isNaN(p) && !isNaN(r) && r !== 0) {
            newResult.current = Math.sqrt(p / r);
          } else if (!isNaN(v) && !isNaN(r) && r !== 0) {
            newResult.current = v / r;
          } else {
            throw new Error("Please provide either power and voltage, power and resistance, or voltage and resistance");
          }
          break;
        case "resistance":
          if (!isNaN(v) && !isNaN(i) && i !== 0) {
            newResult.resistance = v / i;
          } else if (!isNaN(p) && !isNaN(i) && i !== 0) {
            newResult.resistance = p / (i * i);
          } else if (!isNaN(p) && !isNaN(v) && v !== 0) {
            newResult.resistance = (v * v) / p;
          } else {
            throw new Error("Please provide either voltage and current, power and current, or power and voltage");
          }
          break;
        case "energy":
          if (!isNaN(p) && !isNaN(t)) {
            newResult.energy = p * t;
            newResult.power = p;
            newResult.time = t;
          } else {
            throw new Error("Please provide power and time to calculate energy");
          }
          break;
      }
      setResult(newResult);
    } catch (err) {
      if (err instanceof Error) {
        setError(err.message);
      } else {
        setError("An unknown error occurred");
      }
    }
  };

  const handleTabChange = (value) => {
    setActiveTab(value);
    resetForm();
  };

  const getInputFields = () => {
    switch (activeTab) {
      case "power":
        return (
          <>
            <div className="grid gap-2">
              <Label htmlFor="voltage">Voltage (V)</Label>
              <Input id="voltage" type="number" value={voltage} onChange={e => setVoltage(e.target.value)} placeholder="Enter voltage" />
            </div>
            <div className="grid gap-2">
              <Label htmlFor="current">Current (A)</Label>
              <Input id="current" type="number" value={current} onChange={e => setCurrent(e.target.value)} placeholder="Enter current" />
            </div>
            <div className="grid gap-2">
              <Label htmlFor="resistance">Resistance (Ω)</Label>
              <Input id="resistance" type="number" value={resistance} onChange={e => setResistance(e.target.value)} placeholder="Enter resistance" />
            </div>
          </>
        );
      case "voltage":
        return (
          <>
            <div className="grid gap-2">
              <Label htmlFor="power">Power (W)</Label>
              <Input id="power" type="number" value={power} onChange={e => setPower(e.target.value)} placeholder="Enter power" />
            </div>
            <div className="grid gap-2">
              <Label htmlFor="current">Current (A)</Label>
              <Input id="current" type="number" value={current} onChange={e => setCurrent(e.target.value)} placeholder="Enter current" />
            </div>
            <div className="grid gap-2">
              <Label htmlFor="resistance">Resistance (Ω)</Label>
              <Input id="resistance" type="number" value={resistance} onChange={e => setResistance(e.target.value)} placeholder="Enter resistance" />
            </div>
          </>
        );
      case "current":
        return (
          <>
            <div className="grid gap-2">
              <Label htmlFor="power">Power (W)</Label>
              <Input id="power" type="number" value={power} onChange={e => setPower(e.target.value)} placeholder="Enter power" />
            </div>
            <div className="grid gap-2">
              <Label htmlFor="voltage">Voltage (V)</Label>
              <Input id="voltage" type="number" value={voltage} onChange={e => setVoltage(e.target.value)} placeholder="Enter voltage" />
            </div>
            <div className="grid gap-2">
              <Label htmlFor="resistance">Resistance (Ω)</Label>
              <Input id="resistance" type="number" value={resistance} onChange={e => setResistance(e.target.value)} placeholder="Enter resistance" />
            </div>
          </>
        );
      case "resistance":
        return (
          <>
            <div className="grid gap-2">
              <Label htmlFor="power">Power (W)</Label>
              <Input id="power" type="number" value={power} onChange={e => setPower(e.target.value)} placeholder="Enter power" />
            </div>
            <div className="grid gap-2">
              <Label htmlFor="voltage">Voltage (V)</Label>
              <Input id="voltage" type="number" value={voltage} onChange={e => setVoltage(e.target.value)} placeholder="Enter voltage" />
            </div>
            <div className="grid gap-2">
              <Label htmlFor="current">Current (A)</Label>
              <Input id="current" type="number" value={current} onChange={e => setCurrent(e.target.value)} placeholder="Enter current" />
            </div>
          </>
        );
      case "energy":
        return (
          <>
            <div className="grid gap-2">
              <Label htmlFor="power">Power (W)</Label>
              <Input id="power" type="number" value={power} onChange={e => setPower(e.target.value)} placeholder="Enter power" />
            </div>
            <div className="grid gap-2">
              <Label htmlFor="time">Time (hours)</Label>
              <Input id="time" type="number" value={time} onChange={e => setTime(e.target.value)} placeholder="Enter time in hours" />
            </div>
          </>
        );
      default:
        return null;
    }
  };

  const getResultDisplay = () => {
    if (Object.keys(result).length === 0) {
      return (
        <div className="flex flex-col items-center justify-center h-full text-center p-6">
          <Calculator className="h-12 w-12 text-muted-foreground mb-2" />
          <p className="text-muted-foreground">Enter values and calculate to see results</p>
        </div>
      );
    }
    return (
      <div className="space-y-4 p-6">
        {result.power !== undefined && (
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-2">
              <Zap className="h-5 w-5 text-blue-500" />
              <span className="font-medium">Power:</span>
            </div>
            <span className="font-bold text-lg">{result.power.toFixed(2)} W</span>
          </div>
        )}
        {result.voltage !== undefined && (
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-2">
              <Bolt className="h-5 w-5 text-yellow-500" />
              <span className="font-medium">Voltage:</span>
            </div>
            <span className="font-bold text-lg">{result.voltage.toFixed(2)} V</span>
          </div>
        )}
        {result.current !== undefined && (
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-2">
              <Gauge className="h-5 w-5 text-green-500" />
              <span className="font-medium">Current:</span>
            </div>
            <span className="font-bold text-lg">{result.current.toFixed(2)} A</span>
          </div>
        )}
        {result.resistance !== undefined && (
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-2">
              <span className="flex items-center justify-center h-5 w-5 text-purple-500 font-bold">Ω</span>
              <span className="font-medium">Resistance:</span>
            </div>
            <span className="font-bold text-lg">{result.resistance.toFixed(2)} Ω</span>
          </div>
        )}
        {result.energy !== undefined && (
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-2">
              <Clock className="h-5 w-5 text-orange-500" />
              <span className="font-medium">Energy:</span>
            </div>
            <span className="font-bold text-lg">{result.energy.toFixed(2)} Wh</span>
          </div>
        )}
      </div>
    );
  };

  return (
    <div className="flex flex-col md:flex-row gap-6 p-6 max-w-5xl mx-auto">
      <Card className="flex-1 border-border bg-card shadow-md">
        <CardContent className="p-6">
          <div className="flex items-center gap-2 mb-6">
            <Zap className="h-6 w-6 text-primary" />
            <h2 className="text-2xl font-bold">Electrical Calculator</h2>
          </div>
          <Tabs value={activeTab} onValueChange={handleTabChange} className="w-full">
            <TabsList className="grid grid-cols-5 mb-6">
              <TabsTrigger value="power" className="flex items-center gap-1">
                <Zap className="h-4 w-4" />
                <span className="hidden sm:inline">Power</span>
              </TabsTrigger>
              <TabsTrigger value="voltage" className="flex items-center gap-1">
                <Bolt className="h-4 w-4" />
                <span className="hidden sm:inline">Voltage</span>
              </TabsTrigger>
              <TabsTrigger value="current" className="flex items-center gap-1">
                <Gauge className="h-4 w-4" />
                <span className="hidden sm:inline">Current</span>
              </TabsTrigger>
              <TabsTrigger value="resistance" className="flex items-center gap-1">
                <span className="text-xs font-bold">Ω</span>
                <span className="hidden sm:inline">Resistance</span>
              </TabsTrigger>
              <TabsTrigger value="energy" className="flex items-center gap-1">
                <Clock className="h-4 w-4" />
                <span className="hidden sm:inline">Energy</span>
              </TabsTrigger>
            </TabsList>
            <TabsContent value={activeTab} className="mt-0">
              <div className="space-y-4">
                {getInputFields()}
                {error && (
                  <div className="bg-destructive/10 text-destructive p-3 rounded-md text-sm">
                    {error}
                  </div>
                )}
                <div className="flex gap-2 mt-6">
                  <Button onClick={calculatePower} className="flex-1" variant="default">
                    Calculate
                    <ArrowRight className="ml-2 h-4 w-4" />
                  </Button>
                  <Button onClick={resetForm} variant="outline">
                    <RotateCcw className="h-4 w-4" />
                  </Button>
                </div>
              </div>
            </TabsContent>
          </Tabs>
        </CardContent>
      </Card>
      <Card className="flex-1 border-border bg-card shadow-md overflow-hidden">
        <div className="bg-primary/10 p-4 border-b border-border">
          <h3 className="font-semibold text-lg flex items-center gap-2">
            <Calculator className="h-5 w-5" />
            Results
          </h3>
        </div>
        <Separator />
        {getResultDisplay()}
      </Card>
    </div>
  );
};

export default PowerCalculator;
