import './App.css';
import { useState } from 'react';

function App() {
  const [voltage, setVoltage] = useState('');
  const [current, setCurrent] = useState('');
  const [power, setPower] = useState(null);

  const calculatePower = (e) => {
    e.preventDefault();
    const v = parseFloat(voltage);
    const i = parseFloat(current);
    if (!isNaN(v) && !isNaN(i)) {
      setPower(((v * i) / 1000).toFixed(3));
    } else {
      setPower(null);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Power Calculator</h1>
        <form onSubmit={calculatePower} style={{ display: 'flex', flexDirection: 'column', gap: '1rem', alignItems: 'center' }}>
          <div>
            <label>
              Voltage (V):
              <input
                type="number"
                value={voltage}
                onChange={e => setVoltage(e.target.value)}
                placeholder="Enter voltage"
                required
                style={{ marginLeft: '0.5rem' }}
              />
            </label>
          </div>
          <div>
            <label>
              Current (A):
              <input
                type="number"
                value={current}
                onChange={e => setCurrent(e.target.value)}
                placeholder="Enter current"
                required
                style={{ marginLeft: '0.5rem' }}
              />
            </label>
          </div>
          <button type="submit">Calculate Power</button>
        </form>
        {power !== null && (
          <div style={{ marginTop: '1.5rem' }}>
            <h2>Result</h2>
            <p>
              Power = Voltage × Current<br />
              <strong>{voltage} V × {current} A = {power} kW</strong>
            </p>
          </div>
        )}
      </header>
    </div>
  );
}

export default App;
