                          <ScrollReveal key="pkW" baseOpacity={0} enableBlur={true} baseRotation={5} blurStrength={10}>
                            <div className="reveal-answer flex items-center justify-center min-h-[12rem] h-48 md:h-64 text-3xl md:text-4xl font-extrabold text-white text-center">
                              P (kW) = P / 1000 = <b>{(parseFloat(calculated.v) * parseFloat(i)).toFixed(2)}</b> / 1000 = <b>{calculated.p}</b> kW
                            </div>
                          </ScrollReveal>
                        );
                        if (r) {
                          const pIsqR = (Math.pow(parseFloat(i), 2) * parseFloat(r)).toFixed(2);
                          steps.push(
                            <ScrollReveal key="p-isqr-2" baseOpacity={0} enableBlur={true} baseRotation={5} blurStrength={10}>
                              <div className="reveal-answer flex items-center justify-center min-h-[12rem] h-48 md:h-64 text-3xl md:text-4xl font-extrabold text-white text-center">
                                P = IR = <b>{i}</b>  <b>{r}</b> = <b>{pIsqR}</b> W
                              </div>
                            </ScrollReveal>
                          );
                        }
                        if (calculated.v && r) {
                          const pVsqR = (Math.pow(parseFloat(calculated.v), 2) / parseFloat(r)).toFixed(2);
                          steps.push(
                            <ScrollReveal key="p-vsqr-2" baseOpacity={0} enableBlur={true} baseRotation={5} blurStrength={10}>
                              <div className="reveal-answer flex items-center justify-center min-h-[12rem] h-48 md:h-64 text-3xl md:text-4xl font-extrabold text-white text-center">
                                P = V / R = <b>{calculated.v}</b> / <b>{r}</b> = <b>{pVsqR}</b> W
                              </div>
                            </ScrollReveal>
                          );
                        }
                      }
                      // Show energy formulas if relevant
                      if (calculated.p && calculated.t) {
                        steps.push(
                          <ScrollReveal key="energy-short" baseOpacity={0} enableBlur={true} baseRotation={5} blurStrength={10}>
                            <div className="reveal-answer flex items-center justify-center min-h-[12rem] h-48 md:h-64 text-3xl md:text-4xl font-extrabold text-white text-center">
                              E = P  t = <b>{calculated.p}</b>  <b>{calculated.t}</b> = <b>{calculated.e}</b> kWh
                            </div>
                          </ScrollReveal>
                        );
                        steps.push(
                          <ScrollReveal key="energy-long" baseOpacity={0} enableBlur={true} baseRotation={5} blurStrength={10}>
                            <div className="reveal-answer flex items-center justify-center min-h-[12rem] h-48 md:h-64 text-3xl md:text-4xl font-extrabold text-white text-center">
                              Energy (kWh) = Power (kW)  Time (hours) = <b>{calculated.p}</b>  <b>{calculated.t}</b> = <b>{calculated.e}</b> kWh
                            </div>
                          </ScrollReveal>
                        );
                      }
                      if (calculated.v && calculated.i && calculated.t) {
                        const energy = (parseFloat(calculated.v) * parseFloat(calculated.i) * parseFloat(calculated.t) / 1000).toFixed(3);
                        steps.push(
                          <ScrollReveal key="energy-short-vit" baseOpacity={0} enableBlur={true} baseRotation={5} blurStrength={10}>
                            <div className="reveal-answer flex items-center justify-center min-h-[12rem] h-48 md:h-64 text-3xl md:text-4xl font-extrabold text-white text-center">
                              E = V  I  t = <b>{calculated.v}</b>  <b>{calculated.i}</b>  <b>{calculated.t}</b> / 1000 = <b>{energy}</b> kWh
                            </div>
                          </ScrollReveal>
                        );
                        steps.push(
                          <ScrollReveal key="energy-long-vit" baseOpacity={0} enableBlur={true} baseRotation={5} blurStrength={10}>
                            <div className="reveal-answer flex items-center justify-center min-h-[12rem] h-48 md:h-64 text-3xl md:text-4xl font-extrabold text-white text-center">
                              Energy (kWh) = Voltage (V)  Current (A)  Time (hours) / 1000 = <b>{calculated.v}</b>  <b>{calculated.i}</b>  <b>{calculated.t}</b> / 1000 = <b>{energy}</b> kWh
                            </div>
                          </ScrollReveal>
                        );
                      }
                      // Answers
                      steps.push(
                        <ScrollReveal key="ans-v" baseOpacity={0} enableBlur={true} baseRotation={5} blurStrength={10}>
                          <div className="reveal-answer flex items-center justify-center min-h-[12rem] h-48 md:h-64 text-4xl font-extrabold text-white">
                            <span className="font-bold">Voltage (V):</span>&nbsp;{calculated.v}
                          </div>
                        </ScrollReveal>
                      );
                      steps.push(
                        <ScrollReveal key="ans-i" baseOpacity={0} enableBlur={true} baseRotation={5} blurStrength={10}>
                          <div className="reveal-answer flex items-center justify-center min-h-[12rem] h-48 md:h-64 text-4xl font-extrabold text-white">
                            <span className="font-bold">Current (A):</span>&nbsp;{calculated.i}
                          </div>
                        </ScrollReveal>
                      );
                      steps.push(
                        <ScrollReveal key="ans-r" baseOpacity={0} enableBlur={true} baseRotation={5} blurStrength={10}>
                          <div className="reveal-answer flex items-center justify-center min-h-[12rem] h-48 md:h-64 text-4xl font-extrabold text-white">
                            <span className="font-bold">Resistance ():</span>&nbsp;{calculated.r}
                          </div>
                        </ScrollReveal>
                      );
                      steps.push(
                        <ScrollReveal key="ans-p" baseOpacity={0} enableBlur={true} baseRotation={5} blurStrength={10}>
                          <div className="reveal-answer flex items-center justify-center min-h-[12rem] h-48 md:h-64 text-4xl font-extrabold text-white">
                            <span className="font-bold">Power (kW):</span>&nbsp;{calculated.p}
                          </div>
                        </ScrollReveal>
                      );
                      steps.push(
                        <ScrollReveal key="ans-t" baseOpacity={0} enableBlur={true} baseRotation={5} blurStrength={10}>
                          <div className="reveal-answer flex items-center justify-center min-h-[12rem] h-48 md:h-64 text-4xl font-extrabold text-white">
                            <span className="font-bold">Time (hours):</span>&nbsp;{calculated.t}
                          </div>
                        </ScrollReveal>
                      );
                      steps.push(
                        <ScrollReveal key="ans-e" baseOpacity={0} enableBlur={true} baseRotation={5} blurStrength={10}>
                          <div className="reveal-answer flex items-center justify-center min-h-[12rem] h-48 md:h-64 text-4xl font-extrabold text-white">
                            <span className="font-bold">Energy (kWh):</span>&nbsp;{calculated.e}
                          </div>
                        </ScrollReveal>
                      );
                      return steps;
                    })()}
                  </div>
                </div>
              </div>
            </ScrollReveal>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
