import './App.css';
import './index.css';
import { useState } from 'react';

function App() {
  const [voltage, setVoltage] = useState('');
  const [current, setCurrent] = useState('');
  const [resistance, setResistance] = useState('');
  const [calculated, setCalculated] = useState({ v: '', i: '', r: '', p: '' });

  const calculate = (e) => {
    e.preventDefault();
    let v = parseFloat(voltage);
    let i = parseFloat(current);
    let r = parseFloat(resistance);
    let p = null;
    if (!isNaN(v) && !isNaN(i)) {
      r = v / i;
      p = (v * i) / 1000;
    } else if (!isNaN(v) && !isNaN(r)) {
      i = v / r;
      p = (v * i) / 1000;
    } else if (!isNaN(i) && !isNaN(r)) {
      v = i * r;
      p = (v * i) / 1000;
    } else {
      setCalculated({ v: '', i: '', r: '', p: '' });
      return;
    }
    setCalculated({
      v: v.toFixed(2),
      i: i.toFixed(2),
      r: r.toFixed(2),
      p: p.toFixed(3),
    });
  };

  // Reset all fields
  const resetFields = () => {
    setVoltage('');
    setCurrent('');
    setResistance('');
    setCalculated({ v: '', i: '', r: '', p: '' });
  };

  // Count how many inputs are filled
  const filledCount = [voltage, current, resistance].filter(val => val !== '').length;

  // Determine if each input should be disabled
  const disableVoltage = current !== '' && resistance !== '';
  const disableCurrent = voltage !== '' && resistance !== '';
  const disableResistance = voltage !== '' && current !== '';

  return (
    <div className="min-h-screen bg-gray-800 flex flex-col items-center justify-start py-8 px-2">
      <h1 className="text-4xl md:text-5xl font-extrabold text-white mb-8 text-center drop-shadow-lg">Power, Voltage, Current & Resistance Calculator</h1>
      <div className="flex flex-col items-center w-full max-w-xl">
        {/* Visual Diagram */}
        <div className="bg-gray-900 rounded-xl shadow-lg p-4 mb-4 flex flex-col items-center w-full">
          <svg width="370" height="170" className="mb-2">
            {/* Wires */}
            <line x1="50" y1="90" x2="120" y2="90" stroke="#fff" strokeWidth="4" />
            <line x1="190" y1="90" x2="320" y2="90" stroke="#fff" strokeWidth="4" />
            {/* Zigzag resistor */}
            <polyline
              points="120,90 130,80 140,100 150,80 160,100 170,80 180,100 190,90"
              fill="none"
              stroke="#FFD700"
              strokeWidth="4"
            />
            <text x="165" y="112" fill="#FFD700" fontSize="13" textAnchor="middle">Resistor</text>
            {/* Voltage label */}
            <text x="60" y="75" fill="#60A5FA" fontSize="20" fontWeight="bold">V</text>
            <text x="60" y="125" fill="#60A5FA" fontSize="13" textAnchor="middle">Voltage</text>
            {/* Current label and arrow */}
            <text x="330" y="85" fill="#34D399" fontSize="20" fontWeight="bold">I</text>
            <line x1="300" y1="90" x2="320" y2="90" stroke="#34D399" strokeWidth="4" markerEnd="url(#arrowhead)" />
            {/* Current label below */}
            <text x="285" y="115" fill="#34D399" fontSize="14">Current</text>
            {/* Arrowhead marker */}
            <defs>
              <marker id="arrowhead" markerWidth="10" markerHeight="8" refX="10" refY="4" orient="auto">
                <polygon points="0 0, 10 4, 0 8" fill="#34D399" />
              </marker>
            </defs>
          </svg>
          <div className="text-gray-300 text-sm mb-1 text-center">
            <div>Ohm's Law: <b>V = I × R</b></div>
            <div>Power: <b>P = V × I</b> (in Watts, kW = W / 1000)</div>
          </div>
        </div>
        <div className="flex flex-col md:flex-row w-full gap-6 mb-4">
          <form onSubmit={calculate} className="bg-gray-700 rounded-xl shadow-lg p-6 flex flex-col gap-4 w-full max-w-md">
            <div className="flex flex-col gap-1">
              <label className="text-gray-200 font-semibold">Voltage (V):</label>
              <input
                type="number"
                value={voltage}
                onChange={e => setVoltage(e.target.value)}
                placeholder="Enter voltage or leave blank"
                className="rounded-md px-3 py-2 bg-gray-900 text-white focus:outline-none focus:ring-2 focus:ring-blue-400"
                step="any"
                disabled={disableVoltage}
              />
            </div>
            <div className="flex flex-col gap-1">
              <label className="text-gray-200 font-semibold">Current (A):</label>
              <input
                type="number"
                value={current}
                onChange={e => setCurrent(e.target.value)}
                placeholder="Enter current or leave blank"
                className="rounded-md px-3 py-2 bg-gray-900 text-white focus:outline-none focus:ring-2 focus:ring-blue-400"
                step="any"
                disabled={disableCurrent}
              />
            </div>
            <div className="flex flex-col gap-1">
              <label className="text-gray-200 font-semibold">Resistance (Ω):</label>
              <input
                type="number"
                value={resistance}
                onChange={e => setResistance(e.target.value)}
                placeholder="Enter resistance or leave blank"
                className="rounded-md px-3 py-2 bg-gray-900 text-white focus:outline-none focus:ring-2 focus:ring-blue-400"
                step="any"
                disabled={disableResistance}
              />
            </div>
            <div className="text-xs text-gray-400 text-center">Enter any two values to calculate the others.</div>
            <button type="submit" className="mt-2 bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded-lg shadow transition">Calculate</button>
            <button type="button" onClick={resetFields} className="mt-2 bg-gray-500 hover:bg-gray-600 text-white font-bold py-2 px-4 rounded-lg shadow transition">Reset</button>
          </form>
          {calculated.p && (
            <div className="bg-gray-900 rounded-xl shadow-2xl p-6 w-full max-w-md text-white self-start">
              <h2 className="text-2xl font-bold mb-2 text-blue-300">Results</h2>
              <div className="space-y-1 text-lg">
                <div><span className="font-bold">Voltage (V):</span> {calculated.v}</div>
                <div><span className="font-bold">Current (A):</span> {calculated.i}</div>
                <div><span className="font-bold">Resistance (Ω):</span> {calculated.r}</div>
                <div><span className="font-bold">Power (kW):</span> {calculated.p}</div>
              </div>
              <div className="mt-4 text-gray-200 text-base">
                <div className="font-semibold mb-1">Calculation Steps:</div>
                {(() => {
                  const v = voltage;
                  const i = current;
                  const r = resistance;
                  if (v && i) {
                    return (
                      <>
                        <div>R = V / I = <b>{v}</b> / <b>{i}</b> = <b>{calculated.r}</b> Ω</div>
                        <div>P = V × I = <b>{v}</b> × <b>{i}</b> = <b>{(parseFloat(v) * parseFloat(i)).toFixed(2)}</b> W</div>
                        <div>P (kW) = P / 1000 = <b>{(parseFloat(v) * parseFloat(i)).toFixed(2)}</b> / 1000 = <b>{calculated.p}</b> kW</div>
                      </>
                    );
                  } else if (v && r) {
                    return (
                      <>
                        <div>I = V / R = <b>{v}</b> / <b>{r}</b> = <b>{calculated.i}</b> A</div>
                        <div>P = V × I = <b>{v}</b> × <b>{calculated.i}</b> = <b>{(parseFloat(v) * parseFloat(calculated.i)).toFixed(2)}</b> W</div>
                        <div>P (kW) = P / 1000 = <b>{(parseFloat(v) * parseFloat(calculated.i)).toFixed(2)}</b> / 1000 = <b>{calculated.p}</b> kW</div>
                      </>
                    );
                  } else if (i && r) {
                    return (
                      <>
                        <div>V = I × R = <b>{i}</b> × <b>{r}</b> = <b>{calculated.v}</b> V</div>
                        <div>P = V × I = <b>{calculated.v}</b> × <b>{i}</b> = <b>{(parseFloat(calculated.v) * parseFloat(i)).toFixed(2)}</b> W</div>
                        <div>P (kW) = P / 1000 = <b>{(parseFloat(calculated.v) * parseFloat(i)).toFixed(2)}</b> / 1000 = <b>{calculated.p}</b> kW</div>
                      </>
                    );
                  }
                  return null;
                })()}
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
